import React, { useState } from "react";

const Switch = () => {
  const [darkMode, setDarkMode] = useState(false);

  const handleToggle = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle("dark");
  };

  return (
    <div>
      <style>{`
        .theme__icon-part {
          box-shadow: 0.4em -0.4em 0 0.5em hsl(0, 0%, 100%) inset;
        }

        .theme__icon-part ~ .theme__icon-part {
          background-color: hsl(0, 0%, 100%);
          border-radius: 0.05em;
        }

        .theme__icon-part:nth-child(3) {
          transform: rotate(45deg) translateY(0.45em);
        }

        .theme__icon-part:nth-child(4) {
          transform: rotate(90deg) translateY(0.45em);
        }

        .theme__icon-part:nth-child(5) {
          transform: rotate(135deg) translateY(0.45em);
        }

        .theme__icon-part:nth-child(6) {
          transform: rotate(180deg) translateY(0.45em);
        }

        .theme__icon-part:nth-child(7) {
          transform: rotate(225deg) translateY(0.45em);
        }

        .theme__icon-part:nth-child(8) {
          transform: rotate(270deg) translateY(0.5em);
        }

        .theme__icon-part:nth-child(9) {
          transform: rotate(315deg) translateY(0.5em);
        }

        .theme__icon-part.dark {
          opacity: 0;
        }

        .theme__icon-part.dark:nth-child(1) {
          box-shadow: 0.2em -0.2em 0 0.2em hsl(0, 0%, 100%) inset;
          transform: scale(1);
          top: 0.2em;
          left: -0.2em;
        }

        .theme__icon-part.dark:nth-child(2) {
          transform: rotate(45deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(3) {
          transform: rotate(90deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(4) {
          transform: rotate(135deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(5) {
          transform: rotate(180deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(6) {
          transform: rotate(225deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(7) {
          transform: rotate(270deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(8) {
          transform: rotate(315deg) translateY(0.8em);
        }

        .theme__icon-part.dark:nth-child(9) {
          transform: rotate(360deg) translateY(0.8em);
        }
      `}</style>
      <label htmlFor="theme" className="theme flex items-center">
        <span className="relative">
          <input
            id="theme"
            className="theme__toggle appearance-none bg-yellow-200 rounded-xl p-1 w-24 h-12 transition-all transform dark:bg-blue-900"
            type="checkbox"
            role="switch"
            name="theme"
            value="dark"
            onChange={handleToggle}
          />
          <span className="theme__fill bg-gray-900 absolute inset-0 h-full transform -translate-x-full transition-all dark:translate-x-0" />
          <span className="theme__icon absolute top-2 left-2 w-6 h-6">
            <span className="theme__icon-part rounded-full shadow-inner inset-0 w-4 h-4 transition-all transform scale-50 dark:scale-100 dark:top-1 dark:left-[-1px]" />
            {Array.from({ length: 8 }).map((_, index) => (
              <span
                key={index}
                className={`theme__icon-part bg-white rounded-sm absolute transition-all ${
                  darkMode
                    ? "dark"
                    : `rotate-${
                        index * 45
                      } translate-y-2 origin-center w-[0.25rem] h-[0.5rem]`
                }`}
              />
            ))}
          </span>
        </span>
      </label>
    </div>
  );
};

export default Switch;
